import './App.css';
import { useEffect } from 'react';
import { DoTransliterate } from './'

function App() {
   useEffect(() => {
      const jqueryScript = document.createElement('script');
      jqueryScript.src = 'https://ajax.googleapis.com/ajax/libs/jquery/1.7.2/jquery.min.js';
      jqueryScript.async = true;
      document.body.appendChild(jqueryScript);

      const easyTabsScript = document.createElement('script');
      easyTabsScript.src = 'https://bennylin.github.io/transliterasi/js/easytabs.js';
      easyTabsScript.async = true;
      document.body.appendChild(easyTabsScript);

      const transBaliScript = document.createElement('script');
      transBaliScript.src = 'https://jv.wikipedia.org/w/index.php?title=Panganggo:Bennylin/trans-bali.js&action=raw&ctype=text/javascript';
      transBaliScript.async = true;
      document.body.appendChild(transBaliScript);

      const transliteratorScript = document.createElement('script');
      transliteratorScript.src = 'https://bennylin.github.io/transliterasi/js/transliterator-bali.js';
      transliteratorScript.async = true;
      document.body.appendChild(transliteratorScript);

      const scriptScript = document.createElement('script');
      scriptScript.src = 'https://bennylin.github.io/transliterasi/js/script.js';
      scriptScript.async = true;
      document.body.appendChild(scriptScript);

      // function initializeEasyTabs() {
      //   document.getElementById('container').easyTabs({ defaultContent: 1 });
      // }

      // jqueryScript.onload = initializeEasyTabs;

      return () => {
        document.body.removeChild(jqueryScript);
        document.body.removeChild(easyTabsScript);
        document.body.removeChild(transBaliScript);
        document.body.removeChild(transliteratorScript);
        document.body.removeChild(scriptScript);
      };
    }, []);
  return (
   <>
      <div id="container">
         <div id="main_content">
               <div id="home">
                  <div id="bd" role="main">
                     <h1>Cara termudah untuk menulis Aksara Bali*</h1>
                     <div id="transdiv" className="gb">
                        <h3>Pratayang <abbr title="Teks yang Anda ketik/kopipas akan muncul pratayangnya di sini."><span style={{fontFamily: "Arial", fontSize: "smaller"}}>[?]</span></abbr></h3>
                        <div id="prev_label"></div>
                        <p><small>Catatan: "e" menghasilkan taling, "x" menghasilkan pepet. <a href="https://github.com/bennylin/transliterasijawa/issues">Berikan masukan!</a></small></p>
                        <textarea id="inp_txt" className="tb"></textarea>
                     </div>
                     <div id="output" className="gb">
                        <h3>You can compose text here, for transliteration type in textbox above.</h3>
                        <form name="formText">
                           <input type="reset" value="reset" />
                           <textarea id="ta" className="main_ta" name="editSrc"></textarea>
                           <input name="buttonConvert" type="button" value="bali &gt; latin" />
                        </form>
                     </div>
                  </div>
            </div>
         </div>
      </div>
   </>
   );
}

export default App;
